const validationUser = (userData, errors, setErrors) => {
  //USERNAME
  if (!userData.username)
    setErrors({ ...errors, username: "Por favor completa este campo" });
  else if (userData.username.length > 35)
    setErrors({
      ...errors,
      username: "Este campo no puede superar los 35 caracteres",
    });
  else if (
    !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.username)
  ) {
    setErrors({ ...errors, username: "Email inválido" });
  } else {
    setErrors({ ...errors, username: "" });
  }
};

const validationPass = (userData, errors, setErrors) => {
  //PASSWORD

  if (userData.password.length < 6 || userData.password.length > 10) {
    setErrors({
      ...errors,
      password:
        "La longitud del password tiene que ser entre 6 y 10 caracteres",
    });
  } else if (!/\d/.test(userData.password)) {
    setErrors({
      ...errors,
      password: "El password debe contener al menos un número",
    });
  } else {
    setErrors({ ...errors, password: "" });
  }
};

export default { validationUser, validationPass };
