const validation = (userData, errors, setErrors) => {
  //USERNAME
  if (!userData.username)
    setErrors({ ...errors, username: "Por favor completa este campo" });
  else if (userData.username.length > 35)
    setErrors({
      ...errors,
      username: "Este campo no puede superar los 35 caracteres",
    });
  else if (
    !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.username)
  ) {
    setErrors({ ...errors, username: "Email inv치lido" });
  } else {
    setErrors({ ...errors, username: "" });
  }

  //PASSWORD

  if (userData.password.length < 6 || userData.password.length > 10)
    setErrors({
      ...errors,
      username:
        "La longitud del password tiene que ser entre 6 y 10 caracteres",
    });
  else if (
    !/^(?=\w*\d)(?=\w*[A-Z])(?=\w*[a-z])\S{6,10}$/.test(userData.password)
  ) {
    setErrors({ ...errors, username: "Email inv치lido" });
  } else {
    setErrors({ ...errors, username: "" });
  }
};

export default validation;
